.div-table-row{
  /* border: 1px; */
  /* border-style: solid; */
  background: #f5f5f5;
}

/* Clear floats after the columns */
.div-table-row:after {
  content: "";
  display: table;
  clear: both;
}

/* Style the tab */
.tab {
  overflow: hidden;
  /* border: 1px solid #ccc; */
  background-color: #f5f5f5;
  color: #3f6db0;
  padding: 1rem;
}

/* Style the buttons that are used to open the tab content */
.tab > * {
  background-color: inherit;
  float: left;
  font-size: 1.28571rem;
  border: none;
  outline: none;
  cursor: pointer;
  padding: 14px 16px;
  transition: 0.3s;
  color: #3f6db0;
}

/* Change background color of buttons on hover */
.tab button:hover {
  text-decoration: underline;
  background-color: rgba(63, 109, 191, .1);
}

/* Create an active/current tablink class */
.tab button.active, .tab > *:only-of-type {
  background-color: #3f6db0;
  color: white;
}

.view-text .tab + p {
  margin-top: .6rem;
}
